import React, { Component } from "react";
import BankAppService from "../services/bankapp.service";


import AuthService from "../services/auth.service";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

import Accounts from "../components/Accounts.component";
import Balance from "../components/Balance.component";
import UserInfo from "../components/UserInfo.component";


export default class UserAccounts extends Component {
  constructor(props) {
      super(props);

      this.retrieveAccounts  = this.retrieveAccounts.bind(this);
      this.refreshList = this.refreshList.bind(this);

      this.state = {
        currentUser: AuthService.getCurrentUser(),
         accounts: []
      };
    }
     componentDidMount() {
        this.retrieveAccounts(this.state.currentUser.username);
      }

    retrieveAccounts(username) {
        BankAppService.getAccount(username)
          .then(response => {
            this.setState({
              accounts: response.data
            });
            console.log(response.data);
          })
          .catch(e => {
            console.log(e);
          });
      }

    refreshList() {
        this.retrieveTutorials();
      }
    render() {
      const { currentUser } = this.state;

      return (
        <div className="container">
          <header className="jumbotron">
            <h3>
              <strong>{currentUser.username}</strong> 's Home Page
            </h3>
          </header>
          <Link to={"/accounts"} className="container">
               View Bank Accounts
          </Link>
          <Link to={"/balance"} className="container">
               View Balance
          </Link>
          <Link  to={"/userinfo"} className="container">
               Edit User Info
          </Link>
                  <div className="container mt-3">
                      <Switch>
                        <Route exact path="/accounts" component={Accounts} />
                        <Route exact path="/balance" component={Balance} />
                        <Route path="/userinfo" component={UserInfo} />
                      </Switch>
                  </div>


        </div>

      );
    }
}
